import { Link } from "react-router-dom";
//import { BrowserRouter, Routes, Route} from "react-router-dom";
//import Registration from "./Registration";
import React, { useState } from 'react';

function RightPanel(){
    const [showForm, setShowForm] = useState(false);
    const toggleForm = () => {
        setShowForm(!showForm);
    }
    
    return(
        <article className='RightPanel'>
            <h1>Zapisz się na lekcję Twojego języka!</h1>
            <p>Poniżej formularz</p>
            <button onClick={toggleForm}>{showForm ? 'Ukryj formularz' : 'Pokaż formularz'}</button>
            {showForm && (
        <form>
          {/* Tutaj dodaj zawartość formularza */}
          <label>
            Imię:
            <input type="text" />
          </label>
          <label>
            Nazwisko:
            <input type="text" />
          </label>
          {/* Dodaj inne pola formularza */}
          <button type="submit">Zapisz się</button>
        </form>
      )}
        </article>
    );
}

export default RightPanel;
